"time","type":"uint64"},{"internalType":"uint64","name":"duration","type":"uint64"},{"internalType":"uint64","name":"date","type":"uint64"},{"internalType":"address[]","name":"participants","type":"address[]"},{"internalType":"address[]","name":"accepted","type":"address[]"}],"internalType":"struct Calendar.Invite[]","name":"","type":"tuple[]"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"owner","type":"address"},{"internalType":"uint64","name":"_date","type":"uint64"},{"internalType":"uint64","name":"_time","type":"uint64"},{"internalType":"uint64","name":"_duration","type":"uint64"}],"name":"isOwnerAvailable","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint64","name":"_date","type":"uint64"},{"internalType":"uint64","name":"_index","type":"uint64"}],"name":"removeEventByIndex","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint64","name":"_date","type":"uint64"},{"internalType":"uint64","name":"_index","type":"uint64"}],"name":"removeInviteByIndex","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint64","name":"_date","type":"uint64"},{"internalType":"uint64","name":"_index","type":"uint64"},{"internalType":"uint64","name":"_newTime","type":"uint64"},{"internalType":"uint64","name":"_newDuration","type":"uint64"},{"internalType":"bytes32","name":"_swarmLocation","type":"bytes32"}],"name":"updateEventsByDate","outputs":[{"components":[{"internalType":"bytes32","name":"location","type":"bytes32"},{"internalType":"uint64","name":"time","type":"uint64"},{"internalType":"uint64","name":"duration","type":"uint64"}],"internalType":"struct Calendar.Event","name":"","type":"tuple"}],"stateMutability":"nonpayable","type":"function"}],"devdoc":{"kind":"dev","methods":{},"version":1},"userdoc":{"kind":"user","methods":{},"version":1}},"settings":{"compilationTarget":{"contracts/Calendar.sol":"Calendar"},"evmVersion":"istanbul","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":200},"remappings":[]},"sources":{"contracts/Calendar.sol":{"content":"// SPDX-License-Identifier: MIT
// written by @tfius 
pragma solidity ^0.8.0;
/*
 Expected JSON format for event
   [{
        "eventName": "string",
        "description": "string",
        "category": "string",
        "location": "string",
        "participants": ["string"],
        "date": "uint64",
        "duration": "uint64",
        "time": "uint64"
    }]
*/ 
/*
struct Event {
        bytes eventName;
        bytes description;
        bytes category;
        bytes32 location;
        bytes32[] participants;
        uint64 duration;
        uint64 date;
        uint64 time;
    }
*/ 

contract Calendar {
    
    struct Event {
        bytes32 location; // swarm location
        uint64 time;
        uint64 duration;       
    }
    struct Invite {
        address creator;
        bytes32 location; // swarm location
        uint64 time;
        uint64 duration;
        uint64 date;
        address[] participants;
        address[] accepted;
    }
    
    Event[] private _events;
    mapping(address => mapping(uint64 => uint256[])) private _userEvents; // user to date to event index

    Invite[] private _invites;
    mapping(address => mapping(uint64 => uint256[])) private _userInvites; // user to date to invite index

    mapping(address => mapping(address => bool)) private _userAllowList; // allow list per address -> address -> bool
    
    /*  1. The function takes 4 parameters: date, time, duration, swarmLocation.
        2. The first line creates a new Event struct and stores it in the memory variable e. The parameters are passed to the Event struct constructor.
        3. The second line pushes the newly created Event struct to the _events array.
        4. The third line pushes the index of the newly created Event struct to the _userEvents mapping. */
    function addEvent(uint64 _date, uint64 _time, uint64 _duration, bytes32 _swarmLocation) public {
        Event memory e = Event(_swarmLocation, _time, _duration);
        _events.push(e);
        _userEvents[msg.sender][_date].push(_events.length);
    }

    function allowAddress(address _address, bool allow) public {
        _userAllowList[msg.sender][_address] = allow;
    }

    function addEventForAddress(address _address, uint64 _date, uint64 _time, uint64 _duration, bytes32 _swarmLocation) public {
        require(_userAllowList[_address][msg.sender], "Not allowed to add event");
        require(_duration >= 900, "Duration too small > 900s");

        //uint256 _end = _time + _duration;
        //require(_end % 86400 == _time % 86400, "Event must start and end on the same day");

        Event memory e = Event(_swarmLocation, _time, _duration);
        _events.push(e);
        _userEvents[_address][_date].push(_events.length);
