        require(_index < _userEvents[msg.sender][_date].length, "Invalid index");
        Event storage e =  _events[_userEvents[msg.sender][_date][_index]];
        e.time = _newTime;
        e.duration = _newDuration;
        e.location = _swarmLocation;
        return e;
    }
